function App() {


  return (
    <Fragment>
      <header>
        <NavBar />
      </header>
      <main>
        <TodoList todos={todos} />
      </main>

      <footer>
        <TodoForm addTodo={addTodo} />
      </footer>
    </Fragment>
  );
}

export default App;
