  activity:data

})*/

settodos((prev)=>{
  return [
    ...prev,
    {id: Math.random() + 1,
      activity:data}
  ]
})

//console.log(todos)
}

  return (
    <Fragment>
      <header>
        <NavBar />
      </header>
      <main>
        <TodoList todos={todos} />
      </main>

      <footer>
        <TodoForm addTodo={addTodo} />
      </footer>
    </Fragment>
  );
}

export default App;
