import { Fragment,useState } from "react";
import NavBar from "./Components/Layout/NavBar";
import TodoList from "./TO-DO/TodoList";
import TodoForm from "./TO-DO/To-Do-Item/ToDoForm";

/*const todos = [
  {
    id: Math.random() + 9,
    activity: "go outside and play",
  },

  {
    id: Math.random() + 1,
    activity: "need to go to school",
  },

  {
    id: Math.random() + 7,
    activity: "Do homework",
  },
];  */



function App() {

  const[isForm,setForm] = useState(false);
  const [todos,settodos] = useState([]);

  const maintainForm = () =>{
    setForm((prev)=>  !prev)
  }

const addTodo = (data)=>{
/*todos.push({id: Math.random() + 1,
  activity:data

})*/
  settodos((prev)=>{
    return [
      ...prev,
      {id: Math.random() + 1,
        activity:data}
    ]
  })

/*else{
  console.log("length must be greater")
}*/
//console.log(todos)
}

const removeTodo = (data) =>{

  settodos(prev => {
    return prev.filter(todo => todo.id!==data.id)
  })
  //console.log(data)

}

//console.log(todos)

  return (
    <Fragment>
      <header>
        <NavBar maintainForm={maintainForm} />
      </header>
      <main>
      {isForm &&  <TodoForm addTodo={addTodo} />}
        <TodoList todos={todos} removeTodo={removeTodo}/>
      </main>




    </Fragment>
  );
}

export default App;
