import { Fragment,useState } from "react";
import NavBar from "./Components/Layout/NavBar";
import TodoList from "./TO-DO/TodoList";
import TodoForm from "./TO-DO/To-Do-Item/ToDoForm";
import RemovedTodoList from "./TO-DO/To-Do-removedItem/RemovedTodoList";
/*const todos = [
  {
    id: Math.random() + 9,
    activity: "go outside and play",
  },

  {
    id: Math.random() + 1,
    activity: "need to go to school",
  },

  {
    id: Math.random() + 7,
    activity: "Do homework",
  },
];  */



function App() {

  const[isForm,setForm] = useState(true);
  const [todos,settodos] = useState([]);
  const [completedList,setcompletedList] = useState([])
  const [showCompleted,setshowCompleted] = useState(false)

  const maintainForm = () =>{
    setForm((prev)=>  !prev)
  }

const addTodo = (data)=>{
/*todos.push({id: Math.random() + 1,
  activity:data

})*/
  settodos((prev)=>{
    return [
      ...prev,
      {id: Math.random() + 1,
        activity:data}
    ]
  })

/*else{
  console.log("length must be greater")
}*/
//console.log(todos)
}

const removeTodo = (data) =>{
  //console.log(todos)
  //console.log(data)
const deletedAct = todos.filter(todo => todo.id === data.id)
//console.log(deletedAct)

  setcompletedList((prev) =>{
    return [
      ...prev,
      deletedAct
    ]
  })


  settodos(prev => {
    return prev.filter(todo => todo.id!==data.id)
  })
  //console.log(data)

}

const onShowCompletedHandler = () =>{
  setshowCompleted(prev =>!prev)
}

//console.log(todos)

  return (
    <Fragment>
      <header>
        <NavBar maintainForm={maintainForm} onShowCompletedHandler={onShowCompletedHandler} />
      </header>

      <main>
        <TodoList todos={todos} removeTodo={removeTodo}/>
        {isForm &&  <TodoForm addTodo={addTodo} />}
      </main>
      {showCompleted && <RemovedTodoList removedList ={completedList}/> }





    </Fragment>
  );
}

export default App;
