  {
    id: Math.random() + 9,
    activity: "go outside and play",
  },

  {
    id: Math.random() + 1,
    activity: "need to go to school",
  },

  {
    id: Math.random() + 7,
    activity: "Do homework",
  },
];  */

function App() {
  const [isForm, setForm] = useState(true);
  const [todos, settodos] = useState([]);
  const [completedList, setcompletedList] = useState([]);
  const [showCompleted, setshowCompleted] = useState(false);


  useEffect(()=>{

    const fetchData = async () =>{
      const response = await fetch('https://to-do-ea86e-default-rtdb.firebaseio.com/tasks.json')
const responseData = await response.json();

const loadedTask = [];

for(const key in responseData)
{
  loadedTask.push({
    id:key,
    activity:responseData[key].task
  })
  //console.log(loadedTask)
}

settodos(loadedTask)

    }

  fetchData();


  },[todos])

  const maintainForm = () => {
    setForm((prev) => !prev);
  };

  const addTodo = (data) => {

    fetch('https://to-do-ea86e-default-rtdb.firebaseio.com/tasks.json',{
      method:'POST',
      body:JSON.stringify({
       // id: Math.random() + 1,
        task:data

      })
    })
    /*settodos((prev) => {
      return [...prev, { id: Math.random() + 1, activity: data }];
    });*/


  };

  const removeTodo = (data) => {
    //console.log(todos)
    console.log(data)
    const deletedAct = todos.filter((todo) => todo.id === data.id);

    fetch('https://to-do-ea86e-default-rtdb.firebaseio.com/completedtasks.json',{
      method:'POST',
      body:JSON.stringify({
       // id: Math.random() + 1,
        task:data

      })
    })


    fetch(`https://to-do-ea86e-default-rtdb.firebaseio.com/tasks/${data.id}.json`,{
      method: 'Delete',
      headers: {
        'Content-Type': 'application/json',
      },
    })

    console.log(deletedAct)

    useEffect(()=>{

      const response = fetch('https://to-do-ea86e-default-rtdb.firebaseio.com/completedtasks')
      const responseData = response.JSON();

      const CompletedTasks = [];
      for(const key in responseData)
      {
        CompletedTasks.push({
          id:key,
          activity:responseData[key].task
        })
      }

      setcompletedList(CompletedTasks)

    },[])

    // setcompletedList((prev) => {
    //   return [...prev, ...deletedAct];
    // });

   /* settodos((prev) => {
      return prev.filter((todo) => todo.id !== data.id);
    });*/
    //console.log(data)
  };

  const onShowCompletedHandler = () => {
    setshowCompleted((prev) => !prev);
  };

  //console.log(todos)

  return (
    <Fragment>
      <header>
        <NavBar
          maintainForm={maintainForm}
          onShowCompletedHandler={onShowCompletedHandler}
        />
      </header>

      <main>


        <TodoList todos={todos} removeTodo={removeTodo} />
        {isForm && <TodoForm addTodo={addTodo} />}
      </main>
      {showCompleted && <RemovedTodoList removedList={completedList} closeCompletedList={onShowCompletedHandler} />}
    </Fragment>
  );
}

export default App;
